print("""\n<!DOCTYPE html>\n<html>\n<head>\n\t<title>Donald Knuth Chat</title>\n\t<style>\n\t\tbody {\n\t\t\tfont-family: Arial, sans-serif;\n\t\t\tbackground-color: #f2f2f2;\n\t\t}\n\t\t.container {\n\t\t\tmax-width: 800px;\n\t\t\tmargin: 0 auto;\n\t\t\tpadding: 20px;\n\t\t\tbackground-color: #fff;\n\t\t\tbox-shadow: 0 0 10px rgba(0,0,0,0.2);\n\t\t\tborder-radius: 5px;\n\t\t}\n\t\t.message {\n\t\t\tmargin: 10px 0;\n\t\t\tpadding: 10px;\n\t\t\tborder-radius: 5px;\n\t\t}\n\t\t.user {\n\t\t\tbackground-color: #e6f2ff;\n\t\t}\n\t\t.assistant {\n\t\t\tbackground-color: #f2ffe6;\n\t\t}\n\t\t.system {\n\t\t\tbackground-color: #fff2e6;\n\t\t}\n\t\t.input-container {\n\t\t\tdisplay: flex;\n\t\t\tmargin-top: 20px;\n\t\t}\n\t\t.input-text {\n\t\t\tflex-grow: 1;\n\t\t\tpadding: 10px;\n\t\t\tborder-radius: 5px;\n\t\t\tborder: none;\n\t\t\tbox-shadow: 0 0 10px rgba(0,0,0,0.2);\n\t\t\tfont-size: 16px;\n\t\t}\n\t\t.input-submit {\n\t\t\tmargin-left: 10px;\n\t\t\tpadding: 10px;\n\t\t\tborder-radius: 5px;\n\t\t\tborder: none;\n\t\t\tbackground-color: #4CAF50;\n\t\t\tcolor: #fff;\n\t\t\tfont-size: 16px;\n\t\t\tcursor: pointer;\n\t\t\tbox-shadow: 0 0 10px rgba(0,0,0,0.2);\n\t\t}\n\t\t.input-submit:hover {\n\t\t\tbackground-color: #3e8e41;\n\t\t}\n\t</style>\n</head>\n<body>\n\t<div class=\"container\">\n\t\t{% if response_message %}\n\t\t\t<div class=\"message assistant\">{{ response_message }}</div>\n\t\t{% endif %}\n\t\t{% for message in message_array %}\n\t\t\t<div class=\"message {{ message.role }}\">\n\t\t\t\t{{ message.content }}\n\t\t\t</div>\n\t\t{% endfor %}\n\t\t<form method=\"post\">\n\t\t\t<div class=\"input-container\">\n\t\t\t\t<input type=\"text\" name=\"user_input\" class=\"input-text\" placeholder=\"Type your message here...\">\n\t\t\t\t<button type=\"submit\" class=\"input-submit\">Send</button>\n\t\t\t</div>\n\t\t</form>\n\t</div>\n</body>\n</html>\n""")
